import React from "react";
import TextFit from "react-textfit";
import { useRef } from "react";
import * as d3 from "d3";

const CurvedText = ({ children, width, height, radius, dy }) => {
  const ref = useRef();
  const arc = d3
    .arc()
    .outerRadius(radius)
    .innerRadius(0)
    .startAngle(0)
    .endAngle((2 * Math.PI) / 2)
    .cornerRadius(5);

  return (
    <svg width={width} height={height}>
      <path d={arc()} fill="transparent" stroke="black" strokeWidth="2" />
      <g transform={`translate(${width / 2}, ${height / 2})`}>
        <TextFit ref={ref} mode="single">
          {({ width }) => (
            <text
              textAnchor="middle"
              y={dy}
              dy={dy}
              fontSize={width / 1.5}
              fontFamily="Verdana"
            >
              {children}
            </text>
          )}
        </TextFit>
      </g>
    </svg>
  );
};

export default CurvedText;
