import React from "react";
import curveTextPath from "curve-text-path";

const CurvedText = ({ children, width, height, radius, dy }) => {
  const textPath = curveTextPath.path({
    text: children,
    radius: radius,
    x: width / 2,
    y: height - 10,
    curvature: 0.5,
    attrs: {
      fill: "black",
      fontFamily: "Verdana",
      fontSize: `${width * 0.4}px`,
      y: dy,
    },
  });

  return (
    <svg width={width} height={height}>
      <path
        d={`M 0,${radius} A ${radius},${radius} 0 0 1 ${radius},${radius}`}
        fill="transparent"
        stroke="black"
        strokeWidth="2"
      />
      <g>{textPath}</g>
    </svg>
  );
};

CurvedText.propTypes = {
  children: PropTypes.string.isRequired,
  width: PropTypes.number.isRequired,
  height: PropTypes.number.isRequired,
  radius: PropTypes.number.isRequired,
  dy: PropTypes.number.isRequired,
};

export default CurvedText;
